class App extends React.Component {
  constructor(props) {
    super(props)

    let parameters = this.props.sdk.parameters
    let projectId = parameters.installation.projectId
    let contentTypeSlug = parameters.instance.contentTypeSlug
    let isAutoUpdate = parameters.instance.isAutoUpdate

    this.state = {
      projectId: projectId,
      projectUrl:
        "https://" + projectId + "-preview.gtsb.io/" + contentTypeSlug + "/",
      isAutoUpdate: isAutoUpdate,
      webhookUrl:
        "https://backends.ctffns.net/gatsby-preview-proxy/" + projectId,
      debounceInterval: null
    }
  }

  componentDidMount = () => {
    this.detachFns = []

    this.detachFns.push(this.props.sdk.entry.onSysChanged(this.onSysChanged))

    this.props.sdk.window.startAutoResizer()
  }

  componentWillUnmount = () => {
    this.detachFns.forEach(detach => detach())
  }

  constructState = () => {
    return
  }

  onError = error => {
    this.setState({ working: false })
    this.props.sdk.notifier.error(error.message)
  }

  onSysChanged = () => {
    if (!this.state.isAutoUpdate) {
      return
    }
    if (this.state.debounceInterval) {
      clearInterval(this.state.debounceInterval)
    }
    this.state.debounceInterval = setInterval(this.refreshGatsbyPreview, 1000)
  }

  refreshGatsbyPreview = () => {
    clearInterval(this.state.debounceInterval)

    fetch(this.state.webhookUrl, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(
        function(jsonResp) {
          this.props.sdk.notifier.success("Gatsby Preview Updated!")
        }.bind(this),
        function(error) {
          this.props.sdk.notifier.error(error)
        }.bind(this)
      )
  }

  openPreviewTab = () => {
    window.open(
      this.state.projectUrl + this.props.sdk.entry.fields.slug.getValue()
    )
  }
  onChange = e => {
    this.setState({ isAutoUpdate: e.target.value === "yes" }, () => {
      console.log(this.state.isAutoUpdate)
    })
    //e.target.checked = true
  }

  render = () => {
    return (
      <div className="extension">
        <div className="flexcontainer">
          <Forma36.Button buttonType="positive" onClick={this.openPreviewTab}>
            Open preview
          </Forma36.Button>

          {/* <Forma36.FieldGroup>
            <Forma36.CheckboxField
              labelText="Auto update"
              helpText="Any change will refresh the preview"
              disabled={false}
              value="yes"
              onChange={this.onRadioChange}
              checked={this.state.isAutoUpdate}
            />
          </Forma36.FieldGroup> */}

          <Forma36.FieldGroup row={true}>
            <Forma36.RadioButtonField
              labelText="Auto-update"
              disabled={false}
              checked={this.state.isAutoUpdate}
              checked
              value="yes"
              onChange={this.onChange}
              labelIsLight
              id="termsCheckbox"
            />
            <Forma36.RadioButtonField
              labelText="Manual update"
              disabled={false}
              checked={!this.state.isAutoUpdate}
              name="someOption"
              value="no"
              onChange={this.onChange}
              labelIsLight
              id="termsCheckboxOption2"
            />
          </Forma36.FieldGroup>

          <Forma36.Button
            size="small"
            buttonType="muted"
            onClick={this.refreshGatsbyPreview}
          >
            Update Preview
          </Forma36.Button>

          <div
            style={{
              display: "flex",
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "flex-end"
            }}
          >
            <Forma36.Paragraph
              extraClassNames=""
              element="p"
              style={{ marginRight: "5%" }}
            >
              Powered by:
            </Forma36.Paragraph>

            <img
              src="data:image/svg+xml;base64,"
              class="gatsby-logo"
              alt="Gatsby Logo"
              aria-hidden="true"
            />
          </div>
        </div>
      </div>
    )
  }
}

contentfulExtension.init(sdk => {
  ReactDOM.render(<App sdk={sdk} />, document.getElementById("root"))
})
